mapper.update:
update shopping_cart
name = #{name}, image = #{image}, user_id = #{userId}, setmeal_id = #{setmealId}, dish_flavor = #{dishFlavor}, number = #{number}, amount = #{amount}, create_time = #{createTime},
where id = #{id}

mapper.select:
select * from shopping_cart


        and user_id = #{userId}


        and setmeal_id = #{setmealId}


        and dish_id = #{dishId}


        and dish_flavor = #{dishFlavor}
