mapper.update:
update shopping_cart
name = #{name}, image = #{image}, user_id = #{user_id}, setmeal_id = #{setmeal_id}, dish_flavor = #{dish_flavor}, number = #{number}, amount = #{amount}, create_time = #{create_time},
where id = #{id}

mapper.select:
select * from shopping_cart


        and user_id = #{userId}


        and setmeal_id = #{setmealId}


        and dish_id = #{dishId}


        and dish_flavor = #{dishFlavor}
